SELECT fileinfo.file_id AS fileId, fileinfo.file_code AS fileCode, fileinfo.secret_flag AS secretFlag, fileinfo.file_bucket AS fileBucket, fileinfo.file_object_name AS fileObjectName, fileinfo.file_location AS fileLocation, fileinfo.file_origin_name AS fileOriginName, fileinfo.file_suffix AS fileSuffix, fileinfo.file_size_info AS fileSizeInfo, fileinfo.file_version AS fileVersion, suser.real_name AS createUserName, fileinfo.create_time AS createTime, fileinfo.del_flag AS delFlag FROM sys_file_info fileinfo LEFT JOIN sys_user suser ON suser.user_id = fileinfo.create_user
AND fileinfo.file_origin_name LIKE CONCAT('%', #{sysFileInfoRequest.fileOriginName}, '%')
AND fileinfo.file_location = #{sysFileInfoRequest.fileLocation}

SELECT fileinfo.file_id AS fileId, fileinfo.file_code AS fileCode, fileinfo.secret_flag AS secretFlag, fileinfo.file_bucket AS fileBucket, fileinfo.file_object_name AS fileObjectName, fileinfo.file_location AS fileLocation, fileinfo.file_origin_name AS fileOriginName, fileinfo.file_suffix AS fileSuffix, fileinfo.file_size_info AS fileSizeInfo, fileinfo.file_version AS fileVersion, suser.real_name AS createUserName, fileinfo.create_time AS createTime, fileinfo.del_flag AS delFlag FROM sys_file_info fileinfo LEFT JOIN sys_user suser ON suser.user_id = fileinfo.create_user
AND fileinfo.file_origin_name like '%' || #{sysFileInfoRequest.fileOriginName} || '%'
AND fileinfo.file_location = #{sysFileInfoRequest.fileLocation}
